};
const up = () => {
  canvasEditor.up();
};
const upTop = () => {
  canvasEditor.upTop();
};
const down = () => {
  canvasEditor.down();
};
const downTop = () => {
  canvasEditor.downTop();
};

const getList = () => {
  const res = canvasEditor.canvas.getObjects();
  // console.log(res)
  // 不改原数组 反转
  list.value = [
    ...res.filter((item, index) => {
      // 过滤掉辅助线和第一个元素
      return !(item instanceof fabric.GuideLine || item.id === 'workspace' || item.posterType || item.designType);
    }),
  ]
    .reverse()
    .map((item) => {
      const { type, id, name, text } = item;
      return {
        type,
        id,
        name,
        text,
      };
    });
};

onMounted(() => {
  // 当选择画布中的对象时，该对象不出现在顶层。
  canvasEditor.canvas.preserveObjectStacking = true;
  canvasEditor.canvas.on('after:render', getList);
});
</script>

